mapper.insert:
insert into counselor_student (id, school_id, employee_id, 
  student_id, money, status, 
  create_time, update_time, deleted
  )
values (#{id,jdbcType=VARCHAR}, #{schoolId,jdbcType=VARCHAR}, #{employeeId,jdbcType=VARCHAR}, 
  #{studentId,jdbcType=VARCHAR}, #{money,jdbcType=INTEGER}, #{status,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{deleted,jdbcType=BIT}
  )

insert into counselor_student (id, school_id, employee_id,
student_id, money, status,
create_time, update_time, deleted
) VALUES

    (#{stu.id,jdbcType=VARCHAR}, #{stu.schoolId,jdbcType=VARCHAR}, #{stu.employeeId,jdbcType=VARCHAR},
    #{stu.studentId,jdbcType=VARCHAR}, #{stu.money,jdbcType=INTEGER}, #{stu.status,jdbcType=VARCHAR},
    #{stu.createTime,jdbcType=TIMESTAMP}, #{stu.updateTime,jdbcType=TIMESTAMP}, #{stu.deleted,jdbcType=BIT}
    )

mapper.update:
UPDATE counselor_student SET
money = #{money},
status = #{status},
update_time = #{updateTime}
WHERE id = #{id} AND deleted = false;

mapper.select:
SELECT
FROM counselor_student WHERE school_id = #{schoolId} AND employee_id = #{employeeId} AND student_id IN
#{studentId}

SELECT
FROM counselor_student WHERE student_id IN
#{studentId}

SELECT
student_id
FROM counselor_student WHERE employee_id = #{employeId} ORDER BY create_time DESC;
