mapper.update:
UPDATE course_schedule SET
deleted = TRUE,
update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT
employee_id AS employeeId,
sum(study_time) total
FROM course_schedule WHERE


        start_time >= #{startTime}


        AND end_time >= #{endTime}


        AND finish = #{finish}


GROUP BY employee_id ORDER BY total DESC

SELECT employee_id AS employeeId, sum(study_time) total FROM course_schedule WHERE employee_id IN
#{teacherId}
AND finish = #{finish} GROUP BY employee_id ORDER BY total DESC

SELECT
FROM course_schedule WHERE id IN
#{id}

SELECT

FROM course_schedule
WHERE employee_id = #{teacherId} AND finish = #{finish}

    AND end_time >= #{startTime}


    AND end_time <= #{endTime}
