mapper.select:
SELECT

FROM student_pay_log
WHERE employee_id = #{employeeId}

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}

ORDER BY create_time DESC

SELECT employee_id AS employeeId, count(id) AS signNum, SUM(money) AS totalMoney FROM student_pay_log WHERE employee_id IN
#{employeeId}
GROUP BY employeeId ORDER BY totalMoney DESC, signNum DESC

SELECT
employee_id AS employeeId,
SUM(money) AS totalMoney
FROM student_pay_log


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}


        AND op_pay_type = #{type}


GROUP BY employee_id ORDER BY totalMoney DESC

SELECT employee_id AS employeeId, SUM(money) AS totalMoney FROM student_pay_log WHERE id IN
#{employeeId}
AND op_pay_type = #{type}
GROUP BY employee_id ORDER BY totalMoney DESC

SELECT employee_id AS employeeId, FROM student_pay_log WHERE (op_pay_type = 'STUDENTMANAGER_PAY' OR op_pay_type = 'STUDENTMANAGER_BACK')
AND create_time >= #{startTime}
AND create_time <= #{endTime}
GROUP BY employee_id ORDER BY totalMoney DESC

SELECT

FROM student_pay_log
WHERE op_pay_type = #{payType}
AND employeeIds IN

    #{employeeId}


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}
