mapper.select:
SELECT entity_id FROM user


        school_id = #{schoolId}





        AND role_id = #{role};

mapper.update:
UPDATE user SET
name = #{user.name},
phone = #{user.phone},
update_time = #{user.updateTime}
WHERE id = #{user.id} AND deleted = FALSE;

UPDATE `user` SET
password = #{user.password},
salt = #{user.salt},
update_time = #{user.updateTime}
WHERE id = #{user.id}

UPDATE `user` SET
 role_id = #{user.roleId},
 update_time = #{user.updateTime}
 WHERE id = #{user.id}
